import React from "react";
import { useState } from "react";
import { nanoid } from "nanoid";
import Todo from "../components/Todo";
import Form from "../components/Form";
import FilterButton from "../components/FilterButton";

const TodoList = () => {
    const FILTER_MAP = {
        All: () => true,
        Active: (task) => !task.completed,
        Completed: (task) => task.completed,
    }
    const FILTER_NAME = Object.keys(FILTER_MAP)

    const [tasks, setTasks] = useState("")
    const [filter, setFilter] = useState("All")

    const toggleTaskCompleted = (id) => {
        const updateTasks = tasks.map((task) => {
            if (id === task.id) {
                return {...task, completed: !task.completed}
            }
            return task
        })
        setTasks(updateTasks)
    }

    const delTask = (id) => {
        const remainTasks = tasks.filter((task) => id !== task.id)
        setTasks(remainTasks)
    }

    const addTask = (name) => {
        const newTask = {id: `todo-${nanoid()}`, name, completed: false}
        setTasks([...taskList, newTask])
        console.log(taskList)
    }

    const editTask = (id, newName) => {
        const editedTaskList = tasks.map((task) => {
			if (task.id === id) {
				return {...task, name: newName}
			}
			return task
		})

		setTasks(editedTaskList)
    }

    const taskList = tasks.filter(FILTER_MAP[filter]).map((task) => {
		return <Todo 
			id={task.id} 
			completed={task.completed} 
			name={task.name}
			key={task.id}
			toggleTaskCompleted={toggleTaskCompleted}
			delTask={delTask}
			editTask={editTask}
		/>
	})

    const taskNano = taskList.length !== 1 ? "tasks" : "task"
	const headingText = `${taskList.length} ${taskNano} remaining`

	const filterList = FILTER_NAME.map((name) => {

		return	<FilterButton 
				key={name}
				name={name} 
				isPressed={name === filter}
				setFilter={setFilter}
				/>
	})

    return (
        <div className="todoapp stack-large">
          <h1>Todo-List</h1>

          <Form addTask={addTask} />

          <div className="filters btn-group stack-exception">
            {filterList}
          </div>
          <h2 id="list-heading">{headingText}</h2>
          <ul
            role="list"
            className="todo-list stack-large stack-exception"
            aria-labelledby="list-heading">
                    {taskList}
          </ul>
        </div>
      );

}

export default TodoList
